INSERT INTO `news` (`id`, `description`, `image`, `url`, `title`, `pubdate`, `text`) VALUES
(3848, '\n    В ходе учений на полигоне Чебаркуль (Челябинская область) более 1,5 тысяч военных отработали новые мультиконтурные формы ведения боя, связанные с применением единого информационного пространства. Об этом ТАСС сообщил представитель Центрального военного округа Ярослав Рощупкин.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/multicontur/', 'На Южном Урале военные отработали новую тактику ведения боя', 1492764731, NULL),
(3849, '\n    Госдума приняла в третьем, окончательном, чтении закон о поэтапном переводе бюджетников и пенсионеров на банковские карты «Мир». Согласно документу, до 1 июня 2017 года банки должны обеспечить прием карт «Мир» во всех устройствах, предназначенных для осуществления расчетов.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/mir4all/', 'Госдума приняла закон о переводе бюджетников и пенсионеров на карты «Мир»', 1492764780, NULL),
(3850, '\n    Детский лагерь «Береговое» на крымском берегу в Форосе будет восстановлен за счет федерального бюджета. «Он останется детским оздоровительным лагерем. А за счет средств города мы восстановим детский лагерь "Ласпи"», — отметил врио губернатора Севастополя Дмитрий Овсянников. \n  ', NULL, 'https://lenta.ru/news/2017/04/21/kindergarten/', 'Детский лагерь «Береговое» в Форосе восстановят за счет федерального бюджета', 1492765062, NULL),
(3851, '\n    Итальянский дом моды Gucci представил несколько роликов из рекламной кампании на новый сезон. Главная тема — представления людей о будущем и космические путешествия. Серия коротких видео опубликована на официальной странице бренда в Instagram. Героями роликов стали пришельцы.<br />\n  ', NULL, 'https://lenta.ru/news/2017/04/21/gucci/', 'Gucci снял рекламу с пришельцами', 1492765216, NULL),
(3852, '\n    На проходящей в Монако выставке Top Marques продемонстрированы два прототипа летающих автомобилей. Новинки представили голландская компания Pal V и стартап из Словакии AeroMobil. Модель Pal V Liberty оборудована складным ротором и может поступить в продажу уже в конце 2018 года.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/flymonaco/', 'Летающие автомобили за сотни тысяч евро показали в Монако', 1492765260, NULL),
(3853, '\n    В индейском поселении Гарден-Хилл канадской провинции Манитоба 12-летний ребенок застрелил своего сверстника. Полицию вызвали 20 апреля, в 19:30 по местному времени. Мальчики играли с пистолетом, найденным в доме одного из них. Прибывшие на место стражи порядка назвали произошедшее трагической случайностью.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/canada_shooting/', 'В Канаде 12-летний мальчик случайно застрелил друга', 1492765260, NULL),
(3854, '\n    Бывший судья арбитражного суда Тамбовской области Сергей Сучков признан виновным во взяточничестве, покушении на получение взятки и приговорен к 7,5 годам лишения свободы в колонии строгого режима. Юриста, все время следствия находившегося под подпиской о невыезде, взяли под стражу в зале суда сразу после приговора\n  ', NULL, 'https://lenta.ru/news/2017/04/21/judgetaker/', 'Тамбовский арбитражный судья получил семь лет колонии за взятки', 1492765711, NULL),
(3855, '\n    Российский эстрадный певец Иосиф Кобзон считает, что необходимо обязать российские телеканалы и радиостанции транслировать патриотические музыкальные передачи. По словам артиста, для этого каналам необходимо выделить достаточное государственное финансирование. Он отметил, что подобная система действует во Франции.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/kobzon/', 'Кобзон предложил заставить каналы транслировать патриотические программы', 1492765800, NULL),
(3856, '\n    Минобороны готовит предложения в проект Государственной программы вооружения на 2018-2025 годы в части строительства объектов военной инфраструктуры. Об этом, как передает «Интерфакс», заявил глава военного ведомства Сергей Шойгу. Они предусматривают 1740 новых построек.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/gpv_objects/', 'Шойгу раскрыл некоторые планы военного строительства до 2025 года', 1492765800, NULL),
(3857, '\n    Международный коллектив ученых зафиксировал взрыв одной и той же сверхновой звезды, наблюдавшийся сразу в четырех разных участках небесной сферы. Соответствующие исследования опубликованы в журналах Astrophysical Journal Letters и Science, кратко о них сообщает Национальная лаборатория имени Лоуренса в Беркли (США).\n  ', NULL, 'https://lenta.ru/news/2017/04/21/sn/', 'Сверхновая оказалась одновременно в четырех разных местах', 1492766100, NULL),
(3858, '\n    В России скопилось не менее 60 миллиардов тонн бытовых отходов, заявил спецпредставитель президента России по вопросам природоохранной деятельности, экологии и транспорта Сергей Иванов. «Если бы мы были очень маленькой страной, наверное, мы бы раньше задумались, потому что земли бы не осталось», — отметил он. \n  ', NULL, 'https://lenta.ru/news/2017/04/21/garbage/', 'Сергей Иванов подсчитал скопившийся в России мусор', 1492766400, NULL),
(3859, '\n    Городами, в которых чаще всего угоняли автомобили в 2016 году, стали Смоленск, Санкт-Петербург и Курган. Об этом свидетельствует рейтинг, составленный компанией «АльфаСтрахование». За Курганом следуют Кемерово, Ставрополь и Москва, Ярославль, Новосибирск и Оренбург, а также Красноярск. \n  ', NULL, 'https://lenta.ru/news/2017/04/21/ugoni/', 'Составлен рейтинг городов-лидеров по числу автоугонов', 1492766520, NULL),
(3860, '\n    При нападении на приемную управления ФСБ по Хабаровскому краю погиб один сотрудник спецслужбы и посетитель, гражданин одной из стран СНГ, сообщили в ведомстве. Другой посетитель ранен. Неизвестный, открывший стрельбу, ликвидирован. По данным местных СМИ, несколько улиц в центре Хабаровска перекрыты силовиками.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/fsb/', 'В Хабаровске напали на приемную УФСБ и убили одного сотрудника', 1492766640, NULL),
(3861, '\n    Минтранс предлагает установить скидки для дальнобойщиков, которые платят за проезд по федеральным трассам в системе «Платон», в зависимости от пробега большегрузов, заявил первый замминистра транспорта Евгений Дитрих. Предоставлять дисконт нужно владельцам фур, проезжающим в год 60-95 тысяч километров.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/platon_skidki/', 'Минтранс предложил давать скидки в системе «Платон» трудолюбивым дальнобойщикам', 1492767000, NULL),
(3862, '\n    В России впервые успешно выполнены боевые стрельбы берегового ракетного комплекса «Бастион» в условиях Крайнего Севера. Об этом, как передает «Интерфакс», заявил министр обороны Сергей Шойгу. С 2015 года комплекс активно передается отдельным ракетным бригадам береговых войск ВМФ.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/bastion/', 'Военные сообщили об успешных испытаниях комплекса «Бастион» в полярных условиях', 1492767180, NULL),
(3863, '\n    Британский модный дом Alexander McQueen представил новую вариацию классической сумки The Box. Изначально дизайн этой сумки был вдохновлен старинными предметами багажа и сундуками для хранения сокровищ. Модель выполнена из зернистой кожи и выпущена в двух размерах. Внутри — подкладка из мягкой замши.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/the_box/', 'Alexander McQueen переосмыслил дизайн классической сумки', 1492767540, NULL),
(3864, '\n    Служба безопасности Украины (СБУ) проведет антитеррористические учения на границе с Крымом. Мероприятия пройдут в Геническом районе Херсонской области. «Это обычные учения, плановые, которые время от времени проводим», — подчеркнула пресс-секретарь управления СБУ в Херсонской области Виктория Шакула.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/bezpeka/', 'СБУ проведет учения на границе с Крымом', 1492767720, NULL),
(3865, '\n    В Центре общественных связей ФСБ рассказали, что в приемную управления ведомства по Хабаровскому краю вошел неизвестный и, не пересекая зону технического контроля, начал стрелять по людям. В результате погиб сотрудник ФСБ и один из двух посетителей — граждан стран СНГ, второй ранен.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/detali/', 'Появились подробности нападения на приемную УФСБ в Хабаровске', 1492767780, NULL),
(3866, '\n    Оппозиционер Ильдар Дадин потребовал компенсацию за незаконное уголовное преследование и содержание под стражей и домашним арестом в течение двух лет. По словам его адвоката Ксении Костроминой, речь идет о сумме в пять миллионов рублей. Иск был подан в Железнодорожный городской суд Московской области.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/standaccused/', 'Оппозиционер Дадин потребовал пять миллионов рублей за незаконное преследование', 1492767840, NULL),
(3867, '\n    Бывший нападающий «Манчестер Юнайтед» и сборной Тринидада и Тобаго по футболу Дуайт Йорк рассказал о невозможности устроиться на работу тренером из-за цвета кожи. По его словам, с чернокожими специалистами даже не хотят проводить собеседование. Йорк выступал за МЮ с 1998 по 2002 год. <br />\n  ', NULL, 'https://lenta.ru/news/2017/04/21/footballerposetoval/', 'Футболист посетовал на невозможность стать тренером из-за черного цвета кожи', 1492767840, NULL),
(3868, '\n    Правительство Японии обнародовало рекомендации по поведению для граждан страны в случае ракетного нападения. Правила были представлены на состоявшемся в Токио совещании с участием высокопоставленных чиновников аппарата правительства и представителей всех 47 префектур страны.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/japannorthkorea/', 'В Японии опубликовали рекомендации на случай ракетного удара КНДР', 1492768080, NULL),
(3869, '\n    Чешский эстрадный певец Карел Готт продолжит выступления после нескольких перенесенных операций. По словам продюсера, артист был вынужден покинуть сцену из-за борьбы с онкологическим заболеванием. Артист вернется на сцену с концертом «Карел Готт еще жив», в котором примут участие Эва Пиларова и Илона Чакова.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/gott/', 'Чешский певец Карел Готт вернется на сцену после борьбы с раком и операций', 1492768560, NULL),
(3870, '\n    Житель Москвы под ником pashansta показал в Instagram, как его «пылесос панически боится щелей». Молодой человек прошелся насадкой по тумбочке, и, когда машина натыкалась на щели, звучал жалобный крик, который автор наложил поверх видео. Ролик попал на YouTube, где за день собрал около миллиона просмотров. \n  ', NULL, 'https://lenta.ru/news/2017/04/21/cleaner/', 'Москвич снял хоррор с «визжащим от ужаса» пылесосом', 1492768680, NULL),
(3871, '\n    Госдума приняла в третьем, окончательном, чтении закон о разделении кредитных организаций на банки с универсальной и базовой лицензиями. К универсальным организациям будут применять требования ЦБ в полном объеме, а их капитал должен составлять не менее миллиарда рублей. Требования к базовым банкам смягчены.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/twosortsofbanks/', 'Госдума приняла закон о разделении банков на универсальные и базовые', 1492768920, NULL),
(3872, '\n    Госдума приняла в третьем, окончательном чтении закон, запрещающий в имени детей использовать цифры, бранные слова и символы. Также не допускается использование буквенно-цифровых обозначений, указаний на ранги, должности и титулы. Автор инициативы — сенатор Валентина Петренко. <br />\n  ', NULL, 'https://lenta.ru/news/2017/04/21/name/', 'Бранные слова и цифры запретили использовать в детских именах', 1492769100, NULL),
(3873, '\n    Вице-премьер правительства России Дмитрий Козак заявил, что за три года проделана огромная работа по социально-экономической интеграции Крыма с Россией. «Решены первостепенные задачи по преодолению энергетической, транспортной, водной блокады Крыма. В рекордные сроки налажена банковская система», — сказал он.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/kozak/', 'Вице-премьер РФ рассказал о темпах интеграции Крыма', 1492769106, NULL),
(3874, '\n    Франция должна заново оценить свою роль в НАТО и рассмотреть возможность сотрудничества в оборонной сфере с теми странами, которые разделяют интересы Парижа в укреплении безопасности в Европе. Об этом заявил Давид Рашлин, директор предвыборного штаба главы «Национального фронта» Марин Ле Пен.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/le_pen/', 'В предвыборном штабе Ле Пен призвали пересмотреть роль Франции в НАТО', 1492769220, NULL),
(3875, '\n    Жительницу Москвы Екатерину Карамову, которая давала ребенку вместо соски фаллоимитатор, решено отправить на обследование в психиатрическую клинику. Об этом рассказал источник в правоохранительных органах. Женщину допросили следователи. Она утверждает, что использовать секс-игрушку ей посоветовали в поликлинике.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/fallo/', 'Дававшую ребенку дилдо вместо соски москвичку отправили к психиатрам', 1492769225, NULL),
(3876, '\n    Человеком, открывшим стрельбу в приемной управления Федеральной службы безопасности по Хабаровскому краю, оказался местный житель 1999 года рождения по имени А.В. Конев, сообщили в ФСБ. «Имеются данные о его принадлежности к неонацистской группе», — сказали в ведомстве.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/konev/', 'Напавшим на УФСБ в Хабаровске оказался молодой неонацист', 1492769400, NULL),
(3877, '\n    Совет безопасности вряд ли будет рассматривать обращение главы КПРФ Геннадия Зюганова с требованием проверки законопроекта о захоронении Ленина. Об этом заявил постоянный член Совбеза, главы Службы внешней разведки Сергей Нарышкин. По его мнению, для начала необходимо оценить реакцию общественности.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/not_our_topic/', 'В Совбезе усомнились в необходимости обсуждать тему захоронения Ленина', 1492769520, NULL),
(3878, '\n    Более половины российских путешественников готовы отказаться от визитов в США в случае ужесточения правил въезда в страну. Как сообщили в турсервисе Kayak.ru, в ходе опроса тысячи граждан 61 процент респондентов заявили, что не поедут в США, если для этого нужно будет предоставлять пароли от аккаунтов в соцсетях.  \n  ', NULL, 'https://lenta.ru/news/2017/04/21/tourists_usa/', 'Большинство туристов из России заявило о готовности отказаться от поездок в США', 1492770120, NULL),
(3879, '\n    Мадридские «Реал» и «Атлетико» встретятся в полуфинале Лиги чемпионов. Жеребьевка прошла в пятницу, 21 апреля, в штаб-квартире УЕФА в швейцарском Ньоне. Во втором полуфинале «Монако» сыграет с туринским «Ювентусом». Первые матчи пройдут 2 и 3 мая. Ответные поединки — 9 и 10 мая. \n  ', NULL, 'https://lenta.ru/news/2017/04/21/zherebiyovkalch/', 'Определились полуфинальные пары Лиги чемпионов', 1492770180, NULL),
(3880, '\n    Нейробиологи из Пенсильванского университета разработали метод улучшения памяти, основанный на электростимуляции мозга. Оказалось, что благоприятный эффект достигается только в том случае, если воздействие оказывается при низкой активности соответствующих областей центральной нервной системы.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/memory/', 'Найден эффективный способ улучшения памяти электричеством', 1492770420, NULL),
(3881, '\n    Пресс-служба телеканала HBO опубликовала первые фотографии со съемок седьмого сезона американского сериала «Игра престолов». На фотографиях в своих образах запечатлены, в частности, актеры Питер Динклэйдж, Мэйси Уильямс, Эмилия Кларк, Лена Хеди, Николай Костер-Вальдау, Элли Кендрик, Эйдан Гиллен и Конлет Хилл.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/game/', 'Опубликованы первые фотографии со съемок седьмого сезона «Игры престолов»', 1492770780, NULL),
(3882, '\n    Никакой переброски войск к границе с КНДР на юге Приморья не ведется. Об этом говорится в сообщении пресс-службы Восточного военного округа, которое цитирует «Интерфакс». Выход колонн военной техники связан с проведением контрольных проверок боеготовности и завершением масштабных учений ПВО и артиллерии.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/no/', 'Восточный военный округ ответил на сообщения о стягивании войск к границе с КНДР', 1492770780, NULL),
(3883, '\n    Зампредседателя ЦБ Дмитрий Скобелкин сообщил, что регулятор выписал рекордный по величине штраф одному из российских банков за нарушение законодательства в сфере противодействия отмыванию доходов и финансированию терроризма. Ни банк, ни сумму штрафа представитель регулятора не назвал.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/unknownpenalty/', 'ЦБ выписал рекордный штраф неизвестного размера неназванному банку', 1492771440, NULL),
(3884, '\n    Напавший на приемную УФСБ в Хабаровске мужчина был вооружен автоматом и двумя пистолетами. Об этом «Ленте.ру» сообщил источник в силовых структурах. По словам собеседника, нападавший принес с собой автомат Калашникова и два пистолета Ярыгина. В процессе перезаряжания оружия он по неосторожности нанес себе ранение.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/details/', 'Источник рассказал о примененном при нападении на хабаровское УФСБ оружии', 1492771500, NULL),
(3885, '\n    Мужчина, подозреваемый в участии в теракте в Париже и сдавшийся бельгийским властям, не имеет отношения к нападению. «В настоящий момент у нас нет никаких указаний на то, что следы теракта в Париже ведут в Бельгию», — говорится в совместном коммюнике главы МВД Жана Ямбона и руководителя Минюста королевства Коена Генса.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/parisattackman/', 'Подозреваемый в теракте в Париже оказался к нему непричастен', 1492771560, NULL),
(3886, '\n    В Екатеринбургской епархии отрицают появившуюся в СМИ информацию о сносе храма XVIII века. По словам епископа Среднеуральского Евгения, Успенская церковь сейчас находится в состоянии реставрации. Священник отметил, что храм был возвращен монастырю несколько лет назад, и сразу после этого начались ремонтные работы.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/eparch/', 'РПЦ назвала реставрацией снос храма XVIII века в Екатеринбурге', 1492771620, NULL),
(3887, '\n    Президент Белоруссии Александр Лукашенко заявил, что его пытались застрелить за отказ от либеральных реформ. «Я еще совсем сопливым президентом говорил о том, что у нас нет денег рисковать и идти на какие-то реформы. А вдруг провалимся? Через все я прошел — и импичмент, и убить, и застрелить», — сказал он.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/lukashenko/', 'Лукашенко рассказал о попытках его убить за отказ от либеральных реформ', 1492771680, NULL),
(3888, '\n    Ведущий ток-шоу «Прямой эфир» Борис Корчевников в ближайшее время станет руководителем православного телеканала «Спас».  По словам действующего гендиректора канала Бориса Костенко, решение о назначении Корчевникова приняли учредители «Спаса». Сам Костенко продолжит работу на телеканале в качестве его заместителя.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/boris_boris/', 'Ведущий шоу «Прямой эфир» Борис Корчевников возглавит православный канал «Спас»', 1492771740, NULL),
(3889, '\n    Пресс-секретарь президента России Дмитрий Песков не стал комментировать сообщения западных СМИ о переброске военных сил РФ к границе с КНДР на фоне возможного нанесения США удара по Северной Корее. «Вопросы дислокации войск внутри страны не относятся к категории публичных», — заявил представитель Кремля. \n  ', NULL, 'https://lenta.ru/news/2017/04/21/no_comments/', 'Песков отказался комментировать сообщения СМИ о стягивании сил к границе с КНДР', 1492771800, NULL),
(3890, '\n    Российская марка одежды Alexander Terekhov представила новую коллекцию одежды для детей. Вещи представляют собой уменьшенные копии моделей для взрослых. В коллекцию вошли парки, пальто, джинсы, юбки, комбинезоны, платья, блузы и сарафаны. Сроки поступления в продажу и цены пока не сообщались.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/mini_me/', 'Дизайнер Александр Терехов сделал копию взрослой коллекции для детей', 1492771920, NULL),
(3891, '\n    Законопроект о захоронении останков Владимира Ленина призван отвлечь внимание общества от злободневных проблем, считает первый секретарь омского отделения «Коммунистов России» Владимир Жуков. «Как только надо, чтобы народ о чем-то забыл, власть сразу начинает хоронить Ленина. Других причин я не вижу», — сказал он.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/telovojdya/', '«Коммунисты России» сочли идею захоронения Ленина отвлекающим маневром', 1492772040, NULL),
(3892, '\n    В американском штате Флорида задержан мужчина, покусавший собаку. По данным полиции, 30-летний Закари Келли несколько раз укусил за уши питомца своей девушки. Затем он подрался с братом, который вступился за животное, и укусил его за грудь. Подозреваемый утверждает, что хотел проучить собаку за плохое поведение. \n  ', NULL, 'https://lenta.ru/news/2017/04/21/bite/', 'Житель Флориды покусал собаку', 1492773363, NULL),
(3893, '\n    В Кремле поддерживают предложение кандидата в президенты Франции Франсуа Фийона о создании широкой международной коалиции с участием России для борьбы с терроризмом. «Это во многом корреспондируется с той точкой зрения, которая доминирует в Москве», — сказал пресс-секретарь российского президента Дмитрий Песков.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/koalitsiya_fr/', 'В Кремле оценили предложение Фийона объединить усилия против ИГ', 1492773420, NULL),
(3894, '\n    Статистика говорит о снижении преступности в вооруженных силах. Об этом говорится в сообщении Минобороны, поступившем в редакцию «Ленты.ру». Общее количество преступлений сократилось на 5,1 процента. Преступлений, связанных с неуставными отношениями, стало меньше на 37,6 процента, с рукоприкладством — на 16 процентов.<br />\n  ', NULL, 'https://lenta.ru/news/2017/04/21/crimes/', 'Шойгу рассказал об успехах в борьбе с преступностью в вооруженных силах', 1492773425, NULL),
(3895, '\n    В Санкт-Петербурге состоялось официальное открытие нового шоурума Lamborghini. «Россия приобретет статус одного из ключевых рынков для компании на глобальном уровне, и открытие нового шоурума — закономерный шаг для укрепления позиций бренда в регионе», — сказал представитель марки.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/v_pitere/', 'Lamborghini открыл шоурум в Санкт-Петербурге', 1492773625, NULL),
(3896, '\n    Английский «Манчестер Юнайтед» сыграет с испанской «Сельтой» в полуфинале Лиги Европы. Жеребьевка прошла в пятницу, 21 апреля, в штаб-квартире УЕФА в швейцарском Ньоне. Во втором полуфинале французский «Лион» проведет поединок с голландским «Аяксом». Первые матчи пройдут 4 мая, ответные поединки — 11-го.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/europeleague/', '«Манчестер Юнайтед» сыграет с «Сельтой» в полуфинале Лиги Европы', 1492773688, NULL),
(3897, '\n    Американский певец в стиле соул Кьюба Гудинг-старший обнаружен мертвым в своей машине в городе Сан-Фернандо Вэллей, штат Калифорния. По предварительным данным, причиной смерти стала передозировка алкоголем. Музыканту было 72 года. Полиция обнаружила в автомобиле Гудинга пустые бутылки из-под спиртного.\n  ', NULL, 'https://lenta.ru/news/2017/04/21/main_ingredient/', 'Соул-певца Кьюба Гудинга-старшего нашли мертвым в машине', 1492773734, NULL);

